package view;

public class Vertex {
	private double x;
	private double y;
	public double z;

	public Vertex(double x, double y, double z) {
		this.setX(x);
		this.setY(y);
		this.setZ(z);
	}

	public double getX() {
		return x;
	}

	public void setX(double x) {
		this.x = x;
	}

	public double getY() {
		return y;
	}

	public void setY(double y) {
		this.y = y;
	}

	public void setZ(double z) {
		this.z = z;
	}
	public double getZ() {
		// TODO Auto-generated method stub
		return z;
	}


}
